import React, { useState } from 'react';
import Navbar from './navbar';
import Options from './Options';

const Body = () => {
	const [stuffs, setStuffs] = 
   useState([
      {id:0, value: "Beverages", count: 0}, 
      {id:1, value: "Perfumes", count: 0}, 
      {id:2, value: "Breads", count: 0}
   ]);

  //  const [total, setTotal] = useState(0);

  //  const add = () => {
  //   setCount(stuffs.concat({id:2, value: "Breads", count: 0}))
  //  }
  //  setCount(stuff => stuff.concat({id:2, value: "Breads", count: 0}))
		return(
			<div>
				<Navbar/>
				{stuffs.map(stuff => 
          <Options 
            key={stuff.id} 
            value={stuff.value}
            counts={stuff.count}
          />
            )}
			</div>
		);
}

export default Body;
