import React, { useState } from 'react';
import Input from './inputs';
import Navbar from './navbar';
import Options from './Options';

const Body = () => {
  // State for Stuffs
	const [stuffs, setStuffs] = 
   useState([
      {id:0, value: "Beverages", count: 0}, 
      {id:1, value: "Perfumes", count: 0}, 
      {id:2, value: "Breads", count: 0},
      {id: 3, value: "Sardine", count:0}
   ]);
  //  State for Input 
   const [input, setInput] = useState('');

   const handleDelete = id => {
    const newStuffs = stuffs.filter(stuff => stuff.id !== id);
    setStuffs(newStuffs)
   }

  //  const [total, setTotal] = useState(0); -> loop through stuffs, get all counts and add to a variable at the top... :)

  //  Function for Adding new stuffs 
   const add = () => {
   }
  //  Handles Submission of form in Input.jsx 
   const handleSubmit = (e) => {
    e.preventDefault();
    const oldId = stuffs[stuffs.length - 1].id
    const newId = (oldId + 1);
    setStuffs(stuffs.concat({
      id: newId, 
      value: input, 
      count: 0
    }));
    console.log(newId);
    setInput('');
  }

  const handleChange = (value) => {
    setInput(value);
    console.log(input);
    // {()=>{receiveInput(input)}}
  }

  //  setCount(stuff => stuff.concat({id:2, value: "Breads", count: 0}))
		return(
			<div className=' form-control p-2 m-2'>
				<Navbar/>
        <Input 
          handleSubmit={handleSubmit}
          handleChange={handleChange}
        />
				{stuffs.map(stuff => 
          <Options 
            key={stuff.id} 
            stuff={stuff}
            input={input}
            handleDelete={handleDelete}
          />
          )}
        {/* <input
          type="button" 
          className='btn btn-danger btn-sm'
          value="Click" 
          onClick={handleSubmit} 
        /> */}
        {stuffs.length === 0 && <p>There are no Stuffs</p>}
			</div>
		);
}

export default Body;
