import React, { useState } from 'react';

const Options = ({value, counts, add}) => {
  const [count, setCount] = useState()
    return(
      <div>
        <p>{value} 
          <span className="badge badge-warning m-2">
            {count}
          </span> 
          <button
            className='btn btn-danger btn-sm'
            onClick={add}
          >+</button>
        </p>
      </div>
    );
}

export default Options;
