import React from 'react';

const Options = ({stuff, handleDelete, handleAdd}) => {
  // const [count, setCount] = useState(stuff.count);
  // const add = () => {setCount(count + 1); console.log(count)};
    return(
      <div>
        <p>{stuff.value} 
          <span className="badge badge-warning m-2">
            {stuff.count}
          </span> 
          <button
            className='btn btn-primary btn-sm'
            onClick={(stuff) => handleAdd(stuff)}
          >+</button>
          <button
            className='btn btn-danger btn-sm m-2'
            onClick={()=>handleDelete(stuff.id)}
          >Delete</button>
        </p>
      </div>
    );
}

export default Options;
