const Input = ({input, handleInput, handleSubmit}) => {
  return(
    <div>
      <input 
        type="text" 
        value={input}
        placeholder="Notes Go in here..."
        onChange={(e) => handleInput(e.target.value)}
      />
      <button 
        onClick={handleSubmit}
      > Add Notes </button>
    </div>
  );
}

export default Input;
