import React, { useState } from 'react';
import Input from './inputs';
import Navbar from './navbar';
import Options from './Options';

const Body = () => {
	const [stuffs, setStuffs] = 
   useState([
      {id:0, value: "Beverages", count: 0}, 
      {id:1, value: "Perfumes", count: 0}, 
      {id:2, value: "Breads", count: 0},
      {id: 3, value: "Sardine", count:0}
   ]);

   const handleDelete = id => {
    const newStuffs = stuffs.filter(stuff => stuff.id !== id);
    setStuffs(newStuffs)
   }

  //  const [total, setTotal] = useState(0);

   const add = () => {
    setStuffs(stuffs.concat({id:(stuffs[stuffs.length - 1].id + 1), value: "Breads", count: 0}))
   }

  //  setCount(stuff => stuff.concat({id:2, value: "Breads", count: 0}))
		return(
			<div>
				<Navbar/>
        <Input />
				{stuffs.map(stuff => 
        <Options 
          key={stuff.id} 
          stuff={stuff}
          handleDelete={handleDelete}
        />
          )}
        <input type="button" value="Click" onClick={add} />
        {stuffs.length === 0 && <p>There are no Stuffs</p>}
			</div>
		);
}

export default Body;
